    public class ADD : CommandBase<AppSession, StringRequestInfo>
    {

        public override void ExecuteCommand(AppSession session, StringRequestInfo requestInfo)
        {
            Notify.Push(requestInfo.Key + " " + requestInfo.Body);
            session.Send(requestInfo.Parameters.Select(p => Convert.ToInt32(p)).Sum().ToString());
        }
    }

    public static class Notify
    {
        public static event EventHandler OnGetNewMessage;
        public static void Push(string message)
        {
            OnGetNewMessage?.Invoke(null, new Args { Message = message });
        }
    }

    public class Args : EventArgs
    {
        public string Message { get; set; }
    }



